import React, { useContext, useEffect, useState } from 'react'
import { PokemonDetailsContainer, FirstContainer, NameBox, ImagesBox, SecondContainer, ThirdContainer, TypeBox, AttackBox } from './styled'
import Header from '../../components/Header/Header'
import { useParams } from 'react-router-dom'
import GlobalStateContext from '../../global/GlobalStateContext'



const PokemonDetailsPage = () => {

    const [chosedPokemon, setChosedPokemon] = useState({})
    const {name} = useParams()
    const {pokemon} = useContext(GlobalStateContext)

    console.log(chosedPokemon)

    useEffect(()=> {
        const thisPokemon = pokemon.find((poke)=> {
            return poke.name === name
        })
        setChosedPokemon(thisPokemon)
    },[])

    return(
        <div>
            <Header/>
            <PokemonDetailsContainer>
                { chosedPokemon && chosedPokemon.sprites &&
                <FirstContainer>
                    <NameBox>
                        <p>{chosedPokemon && chosedPokemon.name}</p>
                    </NameBox>
                    <ImagesBox>
                        <img src={chosedPokemon.sprites.front_default}/>
                    </ImagesBox>
                    <ImagesBox>
                        <img src={chosedPokemon.sprites.back_default}/>
                    </ImagesBox>
                </FirstContainer>
                }

                <SecondContainer>
                    {chosedPokemon && chosedPokemon.stats && chosedPokemon.stats.map((stat)=> {
                        return (
                            <p key={stat.stat.name}>
                                <strong>{stat.stat.name}:</strong> {stat.base_stat}
                            </p>
                        )
                    })}
                </SecondContainer>
                <ThirdContainer>
                    <TypeBox>
                        {chosedPokemon && chosedPokemon.stats && chosedPokemon.stats.map((stat)=> {
                            return (
                                <p key={stat.stat.name}>
                                    <strong>{stat.stat.name}:</strong> {stat.base_stat}
                                </p>
                            )
                        })}
                    </TypeBox>
                    <AttackBox>
                        Ataques
                    </AttackBox>
                </ThirdContainer>
            </PokemonDetailsContainer>
        </div>
    )
}
export default PokemonDetailsPage
