import React, { useContext } from 'react'
import Header from '../../components/Header/Header'
import PokemonCard from '../../components/PokemonCard/PokemonCard'
import {PokemonCardContainer} from './styled'
import GlobalStateContext from '../../global/GlobalStateContext'
import { goToPokedex } from '../../routes/coordinator'
import { useHistory } from 'react-router-dom'


const HomePage = () => {

    const history= useHistory()
    const {pokemons, setPokemons} = useContext(GlobalStateContext)

    const showMorePokemons = () => {
        const pokemonList = pokemons
        if(pokemonList === 20){
           return pokemonList += 20
        }
        setPokemons(pokemonList)
    }
    
    return(
        <div>
            <Header 
                title={"Lista de Pokémons"}
                HeaderButtonToExchange={() => goToPokedex(history)}
            />
            <button onClick={showMorePokemons}>show more Pokemons</button>

            <PokemonCardContainer>
                {pokemons.map((poke) => {
                    return <PokemonCard key={poke.name} pokemon={poke}/>
                })}

            </PokemonCardContainer>
            
        </div>
    )
}
export default HomePage
