import React, { useContext } from 'react'
import {PokemonCardContainer, ImgContainer, PokemonCardButtons} from './styled'
import {goToPokemonDetails} from '../../routes/coordinator'
import {useHistory} from 'react-router-dom'
import GlobalStateContext from '../../global/GlobalStateContext'

const PokemonCard = ({pokemon, inPokedexPage}) => {

  const history = useHistory()

  const {pokemons, setPokemons, pokedex, setPokedex} = useContext(GlobalStateContext)

  const addPokemon = () => {
    const pokemonIndex = pokemons.findIndex((poke) => poke.name === pokemons.name)
    const newPokeList = [...pokemons]
    newPokeList.splice(pokemonIndex, 1)
    const pokedexList = [...pokedex, pokemons]

    setPokedex(pokedexList)
    setPokemons(newPokeList)

    

  }

  const removePokemon = () => {
    
  }

    return(
        <PokemonCardContainer>
          <ImgContainer>
            <img src={pokekopokemon.sprites && pokemon.sprites.front_default} alt={pokemon.name}/>
          </ImgContainer>
          
          <p>{pokemon.name}</p>
          <PokemonCardButtons>

          <button onClick={inPokedexPage ? removePokemon : addPokemon}>{inPokedexPage ? "Remove" : "Add"}</button>

          <button onClick={()=>goToPokemonDetails(history, pokemon.name)}>Detalhes</button>

          </PokemonCardButtons>
          
          
        </PokemonCardContainer>
    )
}
export default PokemonCard
