import styled from 'styled-components'

export const ContainerErrorPage = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    background-color: #fafafa;
    height: 100vh;
    
`
export const ImageError = styled.div`
    
    
    
        
`
export const TitleError = styled.h1`
    font-family: 'Press Start 2P', cursive;
`
export const GoToHomeErrorPage = styled.button`
    border: none;
    color: yellow;
    background-color: #353434;
    font-family: 'Press Start 2P', cursive;
    box-shadow: 3px 0 #353434,-3px 0 #353434, 0 -3px #353434, 0 3px #353434,6px 0 #353434, -6px 0 #353434, 0 -6px #353434, 0 6px #353434, 0 0 0 3px #353434, 9px 0 black,-9px 0 black,0 9px black,  0 -9px black, 0 -3px 0 3px black, 0 3px 0 3px black,3px 0 0 3px black, -3px 0 0 3px black;
            &:hover{
                cursor:pointer;
                filter: brightness(10%)
            }


`
