import styled from "styled-components";

export const FooterContainer = styled.div`
    display: flex;
    flex-direction: column;
    border: blue solid 1px;

    div:nth-child(2){
        display: flex;
        align-items: center;
        justify-content: center;

        img{
            max-width: 50px;
        }
    }

    div:nth-child(3){
        display: flex;
        align-items: center;
        justify-content: center;

        img{
            max-width: 20px;
            margin-left: 6px;
        }

        img:nth-child(1)
    }
`

export const Mensagem = styled.div`
    padding: 10px;
    width: 40%;
    text-align: justify;

    
`
